import React,{useState}from 'react';

function Form({updateCredit}) {
  const [amount,setAmount]= useState();
  const [desc,setDesc]= useState();

  function handleSubmit(){
    updateCredit(amount,desc);
  }

  function updateDescription(event){
    setDesc(event.target.value)
  }

  function updateAmount(event){
    setAmount(event.target.value)
  }


  return (
    <div>
        <form onSubmit={handleSubmit}>
            <input type="text" onChange={updateDescription} placeholder='Desciption'/>
            <input type="text" onChange={updateAmount} placeholder='Amount'/>
            <button type='submit'>Submit Transactions</button>
        </form>
    </div>
  )
}

export default Form
