import React,{useEffect,useState} from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

import axios from "axios";
import Display from "./Display";
import Form from "./Form"


function Debits() {
  const [objDebit,setObjDebit]=useState("");
  const [desc,setDesc]=useState("");


  /*This API makes a call to the DEBITS API setObjDebit(result) */
  const link="https://bank-of-react-bxbys1cq8-ajlapid718.vercel.app/debits/";
  useEffect(()=>{
    async function fetchDebit(){
      await axios.get(link).then((result)=>{
        setObjDebit(result)
      }).catch((error)=>console.log(error));
    }
    fetchDebit();
  },[]);

  function updateLedger(amount,desc){
    setObjDebit((prev)=>prev-amount);
    setDesc(desc);
  }

  return (
    <div className="Debits">

      <h2>I AM DEBITS</h2>
      <Display data="debit"></Display>
      <Form></Form>

    </div>
  );
}

export default Debits;
